import{_ as e,c as t,o as a,R as r}from"./chunks/framework.8gwMId7n.js";const i="/assets/verdaccio02.ClXhl6hS.png",n="/assets/verdaccio01.BnyPyuWb.png",u=JSON.parse('{"title":"使用verdaccio搭建npm私服","description":"","frontmatter":{},"headers":[],"relativePath":"node/使用verdaccio搭建npm私服.md","filePath":"node/使用verdaccio搭建npm私服.md","lastUpdated":1709194261000}'),c={name:"node/使用verdaccio搭建npm私服.md"},l=r('<h1 id="使用verdaccio搭建npm私服" tabindex="-1">使用verdaccio搭建npm私服 <a class="header-anchor" href="#使用verdaccio搭建npm私服" aria-label="Permalink to &quot;使用verdaccio搭建npm私服&quot;">​</a></h1><h2 id="verdaccio使用说明" tabindex="-1">verdaccio使用说明 <a class="header-anchor" href="#verdaccio使用说明" aria-label="Permalink to &quot;verdaccio使用说明&quot;">​</a></h2><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>Verdaccio 是一个流行的 Node.js 包管理器的代理工具，它允许您在本地或私有网络上轻松地创建和管理 npm 包仓库。通过 Verdaccio，开发团队可以建立自己的 npm 包仓库，以更好地控制和管理其依赖项，同时还可以提供更快速的包下载速度，减轻 npm 中心服务器的负载。</p></div><h2 id="工作原理" tabindex="-1">工作原理 <a class="header-anchor" href="#工作原理" aria-label="Permalink to &quot;工作原理&quot;">​</a></h2><ul><li>代表npm注册表：当我们尝试安装、发布或访问npm包时，verdaccio先查看本地缓存，存在则返回，反之向上游注册表发送亲够，获取包后将其缓存到本地使用。</li><li>本地包存储：满足本地创建和发布自己的npm包</li></ul><h2 id="作用" tabindex="-1">作用 <a class="header-anchor" href="#作用" aria-label="Permalink to &quot;作用&quot;">​</a></h2><ul><li>安全性：Verdaccio 支持访问控制、身份验证和权限管理</li><li>本地开发：可以使用本地缓存，而不必每次都从公共 npm 注册表下载依赖项，从而提高开发效率。方便内容使用</li><li>离线环境：创建本地的npm缓存，不受网络现实</li></ul><h2 id="操作指令" tabindex="-1">操作指令 <a class="header-anchor" href="#操作指令" aria-label="Permalink to &quot;操作指令&quot;">​</a></h2><h1 id="常用工具" tabindex="-1">常用工具 <a class="header-anchor" href="#常用工具" aria-label="Permalink to &quot;常用工具&quot;">​</a></h1><table><thead><tr><th style="text-align:center;">操作</th><th style="text-align:center;">官网文档</th></tr></thead><tbody><tr><td style="text-align:center;">安装</td><td style="text-align:center;">npm i -g vcerdaccio</td></tr><tr><td style="text-align:center;">启动</td><td style="text-align:center;">vcerdaccio</td></tr><tr><td style="text-align:center;">配置私有源</td><td style="text-align:center;">Npm set registry <a href="http://xx.xx.xx.xx:4873" target="_blank" rel="noreferrer">http://xx.xx.xx.xx:4873</a></td></tr><tr><td style="text-align:center;">创建用户</td><td style="text-align:center;">npm adduser --registry <a href="http://xx.xx.xx.xx:4873" target="_blank" rel="noreferrer">http://xx.xx.xx.xx:4873</a></td></tr><tr><td style="text-align:center;">发布npm包</td><td style="text-align:center;">npm adduser --registry <a href="http://xx.xx.xx.xx:4873" target="_blank" rel="noreferrer">http://xx.xx.xx.xx:4873</a></td></tr></tbody></table><h2 id="本地服务搭建" tabindex="-1">本地服务搭建 <a class="header-anchor" href="#本地服务搭建" aria-label="Permalink to &quot;本地服务搭建&quot;">​</a></h2><h3 id="启动" tabindex="-1">启动 <a class="header-anchor" href="#启动" aria-label="Permalink to &quot;启动&quot;">​</a></h3><p><img src="'+i+'" alt="启动"></p><h3 id="web页面" tabindex="-1">web页面 <a class="header-anchor" href="#web页面" aria-label="Permalink to &quot;web页面&quot;">​</a></h3><p><img src="'+n+'" alt="web页面"></p>',15),d=[l];function o(s,x,h,p,m,_){return a(),t("div",null,d)}const g=e(c,[["render",o]]);export{u as __pageData,g as default};
